defmodule One do
  def move(str), do: move(str, 0, 1)

  def move("(" <> str, floor, pos) do
    move(str, floor+1, pos + 1)
  end

  # First moved into basement
  def move(")" <> _str, 0, pos) do
    pos
  end

  def move(")" <> str, floor, pos) do
    move(str, floor-1, pos + 1)
  end
end

